import React, { useEffect, useState } from 'react'
import ProductCard from '../component/ProductCard';
import { Col, Container, Row } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const ProductAll = ({authenticate}) => {

    const navigate = useNavigate();
    const [productList, setProductList] = useState([]);

    const getProducts =async () => {
      let url = `https://my-json-server.typicode.com/mopyour33/hnm-react-router-practice1/products`;
      let response = await fetch(url);
      let data = await response.json();
      setProductList(data);
    }

    const checkLoginUser =(id) => {
      authenticate === false ? navigate("/login") : navigate(`/product/:${id}`);
    }

    useEffect(() => {
      getProducts();
    },[])

  return (
    <div>
        {/* //아이템을 한눈에 볼 수 있도록 만들어주는게 Container라는 컴포넌트임 */}
        <Container className="product-all-content">
          <Row>
            {productList.map((menu) => (
              <Col lg={3} key={menu.id}>
                <ProductCard item={menu} onClick={() => checkLoginUser(menu.id)}/>
              </Col>
            ))}
          </Row>
        </Container>
    </div>
  );
}

export default ProductAll
