import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-regular-svg-icons'
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { json } from 'express';

const Navbar = () => {
  const menuList =[
    "Ïó¨ÏÑ±",
    "Divided",
    "ÎÇ®ÏÑ±",
    "Ïã†ÏÉùÏïÑ/Ïú†ÏïÑ",
    "ÏïÑÎèô",
    "H&M Home",
    "Sales",
    "ÏßÄÏÜçÍ∞ÄÎä•ÏÑ±"
  ];

  const navigate = useNavigate();

  const goToLogin =() => {
    navigate("/login");
  };

  const [searchInfo, setSearchInfo] = useState("");
  const [data, setData] = useState();

  const searchProduct = async(searchInfo) => {

    let url = `http://localhost:5000/products`;
    //let url = `https://my-json-server.typicode.com/mopyour33/hnm-react-router-practice1/products/${id}`;

    let response = await fetch(url);
    let data = await response.json();

    console.log("Data:",data);
    setData(data);


    const filtered = data.filter(item =>
      item.title.includes(searchInfo.toLowerCase())
    );    

    console.log("üîç Í≤ÄÏÉâÎêú Í≤∞Í≥º:", filtered);
    setData(filtered); // ÌïÑÌÑ∞ÎßÅÎêú Îç∞Ïù¥ÌÑ∞Î•º stateÏóê Ï†ÄÏû•ÌïòÎ©¥ ÌïÑÏöîÏãú ÌôîÎ©¥Ïóê ÌëúÏãú Í∞ÄÎä•
  }


  return (
    <div>
      <div>
        <div className="login-button" onClick={goToLogin}>
          <FontAwesomeIcon icon={faUser} />
          <div>Î°úÍ∑∏Ïù∏</div>
        </div>
      </div>
      <div className="nav-section">
        <img width={100} src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTuc3bBMPD3y14ZTzbFpbc3EkEzpg9FjVSM_Q&s" alt=''/>
      </div>
      <div className="menu-area">
        <ul className="menu-list">
          {menuList.map((menu) => <li key={menu}>{menu}</li>)}
        </ul>
        <div className="search-area">
          <FontAwesomeIcon icon={faSearch}/>
          <input className="search-box" type="text" placeholder="Ï†úÌíàÏùÑ Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî" onClick={() => searchProduct(searchInfo)}/>
        </div>
      </div>
    </div>

  )
}

export default Navbar
