import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-regular-svg-icons'
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

const Navbar = () => {
  const menuList =[
    "Ïó¨ÏÑ±",
    "Divided",
    "ÎÇ®ÏÑ±",
    "Ïã†ÏÉùÏïÑ/Ïú†ÏïÑ",
    "ÏïÑÎèô",
    "H&M Home",
    "Sales",
    "ÏßÄÏÜçÍ∞ÄÎä•ÏÑ±"
  ];

  const navigate = useNavigate();

  const goToLogin =() => {
    navigate("/login");
  };

  const [keyword, setKeyword] = useState("");

  const searchProduct = async() => {

    console.log("Í≤ÄÏÉâÏñ¥:",keyword);
    let url = `http://localhost:5000/products`;
    //let url = `https://my-json-server.typicode.com/mopyour33/hnm-react-router-practice1/products/${id}`;

    let response = await fetch(url);
    let data = await response.json();


    // Í≤ÄÏÉâÏñ¥Í∞Ä titleÏóê Ìè¨Ìï®ÎêòÏñ¥ ÏûàÎäî ÏïÑÏù¥ÌÖúÎßå ÌïÑÌÑ∞ÎßÅ (ÎåÄÏÜåÎ¨∏Ïûê Íµ¨Î∂Ñ ÏóÜÏù¥)
    const filtered = data.filter(item =>
      item.title.toLowerCase().includes(keyword.toLowerCase())
    );

    // Ìè¨Ìï®Îêú Ìï≠Î™©Ïùò title Ï∂úÎ†•
    console.log("üîç Í≤ÄÏÉâ Í≤∞Í≥º:");
    filtered.forEach(item => {
      console.log(item);
      data=item;
    });

    //setData(filtered); // ÌïÑÏöîÌïòÎ©¥ ÏÉÅÌÉúÏóê Ï†ÄÏû•ÌïòÏó¨ Í≤ÄÏÉâ Í≤∞Í≥º Î†åÎçîÎßÅ Í∞ÄÎä•

    console.log(`/products/${data?.id}`);
    navigate(`/products/${data?.id}`);
  }

  return (
    <div>
      <div>
        <div className="login-button" onClick={goToLogin}>
          <FontAwesomeIcon icon={faUser} />
          <div>Î°úÍ∑∏Ïù∏</div>
        </div>
      </div>
      <div className="nav-section">
        <img width={100} src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTuc3bBMPD3y14ZTzbFpbc3EkEzpg9FjVSM_Q&s" alt=''/>
      </div>
      <div className="menu-area">
        <ul className="menu-list">
          {menuList.map((menu) => <li key={menu}>{menu}</li>)}
        </ul>
        <div className="search-area">
          <FontAwesomeIcon icon={faSearch} onClick={searchProduct}/>
          <input className="search-box" type="text" placeholder="Ï†úÌíàÏùÑ Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî" value={keyword} onChange={(e) => setKeyword(e.target.value)}/>
        </div>
      </div>
    </div>

  )
}

export default Navbar
