import React, { useEffect, useState } from 'react'
import { Col, Container, Row } from 'react-bootstrap';
import { useParams } from 'react-router-dom'


const ProductDetail = () => {

  const [data,setData] = useState(null);
  const {id} = useParams();


  const getProductDetailInfo = async(id) => {
    
    console.log("id:",id);
    let url = `http://localhost:5000/products/${id}`;
    //let url = `https://my-json-server.typicode.com/mopyour33/hnm-react-router-practice1/products/${id}`;
    console.log(url);

    let response = await fetch(url);
    let data = await response.json();

    console.log("Data:",data);
    setData(data);
    
  }

  useEffect(()=>{
    getProductDetailInfo(id);  
  },[id])

  return (
    <Container>
      <Row className="product-detail-content">
        <Col>
          <img src={data?.img} alt="" className="image-detail-content"/>
        </Col>
        <Col>
          <Row>
            <Col className="info-detail-container">
              <div>{data?.title}</div>
            </Col>
          </Row>
          <Row>
            <Col>
              <div>{data?.price}</div>
            </Col>
          </Row>
          <Row>
            <Col className="info-detail-container">
              <div> {data?.choice===true? "Conscious choice" : ""} </div>
            </Col>
          </Row>
          <Row>
            <Col>
            <div>{data?.new===true ? "신제품" : ""}</div>
            </Col>
          </Row>
          <Row>
            <Col className="purchase-button">
              장바구니 담기
            </Col>
            <Col>
              바로 결제하기
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  )
}

export default ProductDetail
